// Input text to the function to run the finishing move 
// game.pf2eRPGNumbers.finishingMove.generate(moveName)
  new Dialog({
    title: "Finishing Move",
    content: `
      <form>
        <div class="form-group">
          <label for="moveName">Finishing Move Name:</label>
          <input type="text" id="moveName" name="moveName">
        </div>
      </form>
    `,
    buttons: {
      submit: {
        icon: '<i class="fas fa-check"></i>',
        label: "Generate",
        callback: (html) => {
          const moveName = html.find("#moveName").val();
          if (moveName) {
            game.pf2eRPGNumbers.finishingMove.generate(moveName);
          } else {
            ui.notifications.warn("Please enter a finishing move name.");
          }
        }
      },
      cancel: {
        icon: '<i class="fas fa-times"></i>',
        label: "Cancel"
      }
    },
    default: "submit",
    close: () => {}
  }).render(true);
